import React from "react";
import IconList from "../../../common/icon-list/icon-list";

import { faJs } from "@fortawesome/free-brands-svg-icons";
import { faNodeJs } from "@fortawesome/free-brands-svg-icons";
import { faHtml5 } from "@fortawesome/free-brands-svg-icons";
import { faCss3 } from "@fortawesome/free-brands-svg-icons";
import { faSass } from "@fortawesome/free-brands-svg-icons";
import { faAngular } from "@fortawesome/free-brands-svg-icons";
import { faReact } from "@fortawesome/free-brands-svg-icons";
import { faGit } from "@fortawesome/free-brands-svg-icons";
import { faAws } from "@fortawesome/free-brands-svg-icons";
import { faDigitalOcean } from "@fortawesome/free-brands-svg-icons";
import { faWordpressSimple } from "@fortawesome/free-brands-svg-icons";
import { ReactComponent as Firebase } from "../../../../assets/icons/firebase_knockout.svg";
import { ReactComponent as FireStore } from "../../../../assets/icons/firestore.svg";
import { ReactComponent as CloudFunctions } from "../../../../assets/icons/cloud-functions.svg";
import { ReactComponent as RealTimeDB } from "../../../../assets/icons/firebase-realtime-database.svg";

const icons = [faHtml5, faCss3, faSass, faJs, faNodeJs, faAngular, faReact, faGit, faAws, faWordpressSimple, faDigitalOcean];

const Technologies = props => {
  return (
    <section className="motivation text-md-center position-relative pb-7 pb-md-8">
      <div className="container">
        <h2>Technologies</h2>
        <h3 style={{ opacity: 0.4 }}>The tech used in projects at fluxion</h3>
        <IconList icons={icons} size="3x">
          <Firebase className="svg-inline--fa fa-digital-ocean fa-w-16 fa-3x " />
          <FireStore className="svg-inline--fa fa-digital-ocean fa-w-16 fa-3x " />
          <CloudFunctions className="svg-inline--fa fa-digital-ocean fa-w-16 fa-3x " />
          <RealTimeDB className="svg-inline--fa fa-digital-ocean fa-w-16 fa-3x " />
        </IconList>
      </div>
      <img
        className="rellax fly-shape fly-shape_left"
        data-rellax-speed="4"
        src="data:image/svg+xml;utf8;base64,"
      />
      <div className="rellax fly-shape fly-shape_right" data-rellax-speed="-1">
        <img src="data:image/svg+xml;utf8;base64," />
      </div>
    </section>
  );
};

export default Technologies;
